import React, { useState } from 'react'


const Button = (props) => {
  return (
    <button onClick={() => props.ftype(props.counter+1)} > {props.counter}</button>
  )
}
const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div> 
      <Button ftype={setGood} counter={good}/>
      <Button ftype={setNeutral} counter={neutral}/>
      <Button ftype={setBad} counter={bad}/>
    </div>
  )
}

export default App
