import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '8707437375'}
  ]) 
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const addName = (event) => {
    event.preventDefault()
    let newObj = {name: newName, number: newNumber}

    let allNames = persons.map((object)=>object.name)
    if(allNames.indexOf(newName) !== -1){
      alert(`${newName} is already added to phonebook.`)
      return 
    }

    let tmp = [...persons]
    tmp.push(newObj);

    setPersons(tmp);
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addName}>
        <div>
          name: <input onChange={(event)=>{setNewName(event.target.value)}}/>
          number: <input onChange={(event)=>{setNewNumber(event.target.value)}}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map((person,key) => <p key={key}>{person.name} {person.number}</p>)}
    </div>
  )
}

export default App
